'books', 'CREATE TABLE `books` (\n  `idbooks` int(11) NOT NULL AUTO_INCREMENT,\n  `book` varchar(45) NOT NULL,\n  `author` varchar(45) NOT NULL,\n  `data` varchar(45) NOT NULL,\n  `url` varchar(45) NOT NULL,\n  `genre` varchar(45) NOT NULL,\n  `pages` varchar(45) NOT NULL,\n  `stock` int(11) NOT NULL,\n  PRIMARY KEY (`idbooks`,`book`,`author`,`data`,`url`,`genre`,`pages`,`stock`)\n) ENGINE=InnoDB AUTO_INCREMENT=8 DEFAULT CHARSET=utf8'
'genres', 'CREATE TABLE `genres` (\n  `idgenres` int(11) NOT NULL AUTO_INCREMENT,\n  `genre` varchar(45) NOT NULL,\n  PRIMARY KEY (`idgenres`,`genre`)\n) ENGINE=InnoDB AUTO_INCREMENT=7 DEFAULT CHARSET=utf8'
'user', 'CREATE TABLE `user` (\n  `id` int(11) NOT NULL AUTO_INCREMENT,\n  `name` varchar(45) NOT NULL,\n  `pass` varchar(45) NOT NULL,\n  `mail` varchar(45) NOT NULL,\n  PRIMARY KEY (`id`)\n) ENGINE=InnoDB AUTO_INCREMENT=8 DEFAULT CHARSET=utf8'
'requests', 'CREATE TABLE `requests` (\n  `id` int(11) NOT NULL AUTO_INCREMENT,\n  `title` varchar(45) NOT NULL,\n  `user` varchar(45) NOT NULL,\n  `status` varchar(45) NOT NULL,\n  PRIMARY KEY (`id`,`title`,`user`,`status`)\n) ENGINE=InnoDB AUTO_INCREMENT=57 DEFAULT CHARSET=utf8'
